SELECT Client.client_id, Client.first_name, Client.last_name, COUNT(Transaction.transaction_id) FROM Client JOIN Client_account ON Client.client_id = Client_account.client_id JOIN Account ON Client_account.account_id = Account.account_id JOIN Transaction ON Account.account_id = Transaction.from_account WHERE Transaction.amount > 150 GROUP BY Client.client_id, Client.first_name, Client.last_name ORDER BY COUNT(Transaction.transaction_id) DESC LIMIT 3;